import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import styled from "styled-components";
import Question from "../components/Question";
import StudentSideBar from "../components/StudentSideBar";

import { nextQuestion, showResults } from "../redux/quizSlice";

function QuizPage() {
  // const [questions, setQuestions] = useState(quiz);
  const [currentQuestionId, setCurrentQuestionId] = useState(1);

  const { questions, showResults, answers } = useSelector(
    (state) => state.quiz
  );
  console.log(answers);
  const dispatch = useDispatch();

  return (
    <Container>
      <StudentSideBar />
      <QuizContainer>
        {showResults && <Results>Show results</Results>}
        {!showResults && (
          <>
            <Question />
            <NextButton onClick={() => dispatch(nextQuestion())}>
              Next Question
            </NextButton>
          </>
        )}
      </QuizContainer>
    </Container>
  );
}

export default QuizPage;

const Container = styled.main`
  display: flex;
  color: #101f3c;
`;

const QuizContainer = styled.section`
  flex: 8;
  display: flex;
  align-items: center;
  flex-direction: column;
  justify-content: center;
`;

const NextButton = styled.article`
  margin: 0 auto;
  color: #fff;
  background: #0b0e21;
  width: 300px;
  font-size: 18px;
  font-weight: 600;
  padding: 14px 75px;
  text-transform: uppercase;
  cursor: pointer;
  text-align: center;
`;

const Results = styled.article``;
