import styled from "styled-components";

const Answer = ({
  answerText,
  index,
  onSelectedAnswer,
  currentAnswer,
  correctAnswer,
}) => {
  const letterMapping = ["A", "B", "C", "D"];
  const selectedAnswer = currentAnswer === answerText;
  const selectClass = selectedAnswer ? "selected" : "";
  const disableClass = currentAnswer ? "disabled" : "";

  return (
    <AnswerText
      className={`${selectClass} ${disableClass}`}
      onClick={() => onSelectedAnswer(answerText)}
    >
      {letterMapping[index]}. {answerText}
    </AnswerText>
  );
};

export default Answer;

const AnswerText = styled.main`
  background-color: ${(props) =>
    props.className === "selectClass" ? "red" : "#f5f5f5"};
  padding: 0.7rem;
  cursor: pointer;
  color: gray;
  border-top-left-radius: 5px;
  border-top-right-radius: 5px;
  &:hover {
    background-color: lightgray;
  }
`;
const AnswerLetter = styled.main``;
